package com.fitanywhere.forumpost.controller;

import java.io.IOException;

import javax.servlet.ServletOutputStream;
import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;

import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Controller;
import org.springframework.web.bind.annotation.GetMapping;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.RequestParam;

import com.fitanywhere.forumpost.model.ForumPostService;

@Controller
@RequestMapping("/forumpost")
public class ForumPostDBGifReaderController {
	
	@Autowired
	ForumPostService forumPostSvc;
	
	/*
	 * This method will serve as listOneEmp.html , listAllEmp.html handler.
	 */
	@GetMapping("DBGifReader")
	public void dBGifReader(@RequestParam("fpId") String fpId, HttpServletRequest req, HttpServletResponse res)
			                                                                                          throws IOException {
		res.setContentType("image/gif");
		ServletOutputStream out = res.getOutputStream();

		try {
			out.write(forumPostSvc.getOneForumPost(Integer.valueOf(fpId)).getFpPic());
		} catch (Exception e) {
	        byte[] buf = java.util.Base64.getDecoder().decode("");
			out.write(buf);
		}
	}
}